mapper.update:
update orders

    cancel_reason = #{cancelReason},
    cancel_time = #{cancelTime},
    status = #{status}

where id =#{id}

mapper.select:
select * from orders


        and user_id = #{userId}


        and status = #{status}


        and order_time between #{beginTime} and #{endTime}


        and phone = #{phone}


order by order_time desc

select * from orders

    and id=#{id}
    number = #{numbers}

select count(*) from orders

    and order_time > #{startTime}
    and order_time < #{endTime}
    and status = #{status}

select od.name name, SUM(od.number) number from  orders o left join order_detail od on o.id = od.order_id

    and status = 5
    and order_time > #{beginTime}
    and order_time < #{endTime}

group by od.name order by number desc limit 0,10

select sum(amount) from orders

    and order_time > #{begin}
    and order_time < #{end}
    and status = 5
